mapper.select:
SELECT gv.* FROM (
SELECT gv.*, ROW_NUMBER() OVER (ORDER BY regdate desc) rown FROM GRPURCHASEVIEW gv WHERE gv.memid=#{memId}
) gv WHERE rown   >=  #{start}  AND rown   <=  #{end} ORDER BY regdate desc

SELECT gv.* FROM (
SELECT gv.*, ROW_NUMBER() OVER (ORDER BY regdate desc) rown FROM GRPURCHASEVIEW gv WHERE (regdate BETWEEN #{firstdate} AND #{seconddate}) AND memId = #{memId}
) gv WHERE rown  >=  #{start}  AND rown   <=  #{end} ORDER BY regdate desc
