mapper.select:
        SELECT p.*, s.name storagename from 
        (SELECT purchaseno no, participant, deadline, storageno FROM (SELECT *, ROW_NUMBER() OVER 
	    (PARTITION BY PURCHASENO ORDER BY no desc) recent 
						FROM purchaseview p WHERE itemno = #{itemno} 
						ORDER BY PURCHASENO DESC 
	     ) WHERE recent=1 ORDER BY deadline DESC 
        ) p 
	    INNER JOIN STORAGE s on p.storageno=s.NO
